INSERT INTO [CMS_WebPart] ([WebPartID], [WebPartName], [WebPartDisplayName], [WebPartDescription], [WebPartFileName], [WebPartProperties], [WebPartCategoryID], [WebPartParentID], [WebPartDocumentation], [WebPartGUID], [WebPartLastModified], [WebPartType], [WebPartLoadGeneration], [WebPartDefaultValues], [WebPartResourceID], [WebPartCSS], [WebPartSkipInsertProperties], [WebPartThumbnailGUID]) VALUES (680, N'SearchFilter', N'Smart search filter', N'Displays a filter that can be connected to smart search web parts and allows users to limit the range of their searches or set the order in which results are shown. Filtering options can either be specified manually or via a predefined SQL query.', N'SmartSearch/SearchFilter.ascx', N'<form><category name="Filter settings" caption="Filter properties" visible="True" /><field column="SearchWebpartID" fieldcaption="Search dialog webpart ID" visible="true" columntype="text" fieldtype="CustomUserControl" allowempty="true" columnsize="200" fielddescription="Specifies the ID of the Smart search dialog or Smart search dialog with results web part to which the filter should be connected. The web part must be on the same page." publicfield="false" spellcheck="false" guid="4724c9ef-63ad-41f0-9474-9c32c568534b" visibility="none"><settings><Trim>False</Trim><AutoCompleteFirstRowSelected>False</AutoCompleteFirstRowSelected><AutoCompleteShowOnlyCurrentWordInCompletionListItem>False</AutoCompleteShowOnlyCurrentWordInCompletionListItem><FilterMode>False</FilterMode><AutoCompleteEnableCaching>False</AutoCompleteEnableCaching><controlname>textboxcontrol</controlname></settings></field><field column="FilterMode" fieldcaption="Filter mode" visible="true" columntype="text" fieldtype="CustomUserControl" allowempty="true" columnsize="50" fielddescription="Sets the type of user interface elements that should be used to select the filtering options. Possible options are checkboxes, radio buttons, a drop-down list or a text box." publicfield="false" g